CREATE TABLE IF NOT EXISTS USERS (username VARCHAR PRIMARY KEY, common_name VARCHAR NOT NULL, passwd VARCHAR(64) NOT NULL, colour VARCHAR, img_url CLOB);
CREATE TABLE IF NOT EXISTS USER_GROUPS (id INTEGER PRIMARY KEY AUTO_INCREMENT, username VARCHAR NOT NULL, group_name VARCHAR NOT NULL);
CREATE TABLE IF NOT EXISTS USER_CONNECTIONS (id INTEGER PRIMARY KEY AUTO_INCREMENT, source_username VARCHAR, target_username VARCHAR);
CREATE TABLE IF NOT EXISTS CHAT_MESSAGE (id INTEGER PRIMARY KEY AUTO_INCREMENT, content VARCHAR, source_username VARCHAR, target_username VARCHAR, created VARCHAR);


